import React, { Component } from 'react'
import ClientService from '../services/ClientService'

class ViewClientComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            client: {}
        }
    }

    componentDidMount(){
        ClientService.getClientById(this.state.id).then( res => {
            this.setState({client: res.data});
        })
    }

    render() {
        return (
            <div>
                <br></br>
                <div className = "card col-md-6 offset-md-3">
                    <h3 className = "text-center"> View Clients Details</h3>
                    <div className = "card-body">
                        <div className = "row">
                            <label> Client Name: </label>
                            <div> { this.state.client.Name }</div>
                        </div>
                        <div className = "row">
                            <label> Client Email ID: </label>
                            <div> { this.state.client.emailId }</div>
                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

export default ViewClientComponent;
