import React, { useState } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";

function Calendar() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [workLocation, setWorkLocation] = useState("");

  const handleDateClick = (info) => {
    setSelectedDate(info.dateStr);
  };

  const handleWorkLocationChange = (event) => {
    setWorkLocation(event.target.value);
  };

  const handleValidation = () => {
    if (!selectedDate) {
      console.error("No date selected");
      return;
    }
    if (!workLocation) {
      console.error("No work location selected");
      return;
    }
    console.log("Validation Passed");
    console.log("Selected Date:", selectedDate);
    console.log("Work Location:", workLocation);
  };

  return (
    <div>
      {" "}
      {selectedDate && (
        <div>
          <h3>Selected Date: {selectedDate}</h3>
          <select value={workLocation} onChange={handleWorkLocationChange}>
            <option value="">Select Work Location</option>
            <option value="home">Work from Home</option>
            <option value="office">Work in the Office</option>
            <option value="client">Work at Client Sites</option>
          </select>
          <button onClick={handleValidation}>Validate</button>
        </div>
      )}
      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        selectable={true}
        dateClick={handleDateClick}
      />
    </div>
  );
}

export default Calendar;
