import React from "react";
import Dashboard from "./pages/Dashboard";
import DashboardAdmin from "./pages/DashboardAdmin";

// 1. import `NextUIProvider` component
import { NextUIProvider } from "@nextui-org/react";

import Login from "./pages/Login";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from "./Components/Header";

import ListEmployeeComponent from "./Components/ListEmployeeComponent";
import ViewEmployeeComponent from './Components/ViewEmployeeComponent';

import ListClientComponent from "./Components/ListClientComponent";
import ViewClientComponent from "./Components/ViewsClientComponent";

import TableClientComponent from "./Components/TableClientComponent";
import TableEmployeeComponent from "./Components/TableEmployeeComponent";

function App() {

  return (
    <NextUIProvider>
      <div className="App">

        <Router>

          <Header />
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/Dashboard" element={<Dashboard />} />


            <Route path="/DashboardAdmin" element={<DashboardAdmin />} />



            <Route path="/employees" element={<ListEmployeeComponent />} />
            <Route path="/view-employee/:id" element={<ViewEmployeeComponent />} />

            <Route path="/clients" element={<ListClientComponent />} />
            <Route path="/view-client/:id" element={<ViewClientComponent />} />

          </Routes>
        </Router>

      </div>
    </NextUIProvider>
  );

}
export default App;

