import React, { Component } from 'react'
import EmployeeService from '../services/EmployeeService'

class ListEmployeeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                employees: []
        }
        this.addEmployee = this.addEmployee.bind(this);
        this.editEmployee = this.editEmployee.bind(this);
        this.deleteEmployee = this.deleteEmployee.bind(this);
    }

    deleteEmployee(id){
        EmployeeService.deleteEmployee(id).then( res => {
            this.setState({employees: this.state.employees.filter(employee => employee.id !== id)});
        });
    }
    viewEmployee(id){
        this.props.history.push(`/view-employee/${id}`);
    }
    editEmployee(id){
        this.props.history.push(`/add-employee/${id}`);
    }

    componentDidMount(){
        EmployeeService.getEmployees().then((res) => {
            this.setState({ employees: res.data});
        });
    }

    addEmployee(){
        this.props.history.push('/add-employee/_add');
    }

    render() {
        return (
            <div className="container mx-auto">
                <h2 className="text-center text-2xl font-bold mb-4">Employees List</h2>
                <div className="flex justify-end mb-4">
                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" onClick={this.addEmployee}>Add Employee</button>
                </div>
                <table className="w-full border-collapse">
                    <thead>
                        <tr>
                            <th className="border border-gray-400 px-4 py-2">Employee First Name</th>
                            <th className="border border-gray-400 px-4 py-2">Employee Last Name</th>
                            {/* <th className="border border-gray-400 px-4 py-2">Employee Email Id</th> */}
                            <th className="border border-gray-400 px-4 py-2">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.employees.map(employee => (
                            <tr key={employee.id}>
                                <td className="border border-gray-400 px-4 py-2">{employee.firstName}</td>
                                <td className="border border-gray-400 px-4 py-2">{employee.lastName}</td>
                                <td className="border border-gray-400 px-4 py-2">{employee.emailId}</td>
                                <td className="border border-gray-400 px-4 py-2">
                                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2" onClick={() => this.editEmployee(employee.id)}>Update</button>
                                    <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mr-2" onClick={() => this.deleteEmployee(employee.id)}>Delete</button>
                                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" onClick={() => this.viewEmployee(employee.id)}>View</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default ListEmployeeComponent;
