import axios from 'axios';

const EMPLOYEE_API_BASE_URL = "http://localhost:5000/api/v1/employees";

    axios.get('/user/12345')
 .catch(function (error) {
    if (error.response) {
      // La requête a été faite et le serveur a répondu avec un code d'état
      // qui sort de la plage de 2xx
      console.log(error.response.data);
      console.log(error.response.status);
      console.log(error.response.headers);
    } else if (error.request) {
      // La requête a été faite mais aucune réponse n'a été reçue
      // `error.request` est une instance de XMLHttpRequest dans le navigateur et une instance de
      // http.ClientRequest dans node.js
      console.log(error.request);
    } else {
      // Quelque chose s'est passé lors de la configuration de la requête qui a déclenché une erreur
      console.log('Error', error.message);
    }
    console.log(error.config);
 });

class EmployeeService {

    getEmployees(){
        return axios.get(EMPLOYEE_API_BASE_URL);
    }

    createEmployee(employee){
        return axios.post(EMPLOYEE_API_BASE_URL, employee);
    }

    getEmployeeById(employeeId){
        return axios.get(EMPLOYEE_API_BASE_URL + '/' + employeeId);
    }

    updateEmployee(employee, employeeId){
        return axios.put(EMPLOYEE_API_BASE_URL + '/' + employeeId, employee);
    }

    deleteEmployee(employeeId){
        return axios.delete(EMPLOYEE_API_BASE_URL + '/' + employeeId);
    }
}

export default new EmployeeService()
