import React, { Component } from 'react'
import ClientService from '../services/ClientService';

class ListClientComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                clients: []
        }
        this.addClient = this.addClient.bind(this);
        this.editClient = this.editClient.bind(this);
        this.deleteClient = this.deleteClient.bind(this);
    }

    deleteClient(id){
        ClientService.deleteClient(id).then( res => {
            this.setState({clients: this.state.clients.filter(client => client.id !== id)});
        });
    }
    viewClient(id){
        this.props.history.push(`/view-client/${id}`);
    }
    editClient(id){
        this.props.history.push(`/add-client/${id}`);
    }

    componentDidMount(){
        ClientService.getClients().then((res) => {
            this.setState({client: res.data});
        });
    }

    addClient(){
        this.props.history.push('/add-client/_add');
    }

    render() {
        return (
            <div className="container mx-auto">
                <h2 className="text-center text-2xl font-bold mb-4">Clients List</h2>
                {/* <div className="flex justify-end mb-4">
                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" onClick={this.addEmployee}>Add Employee</button>
                </div> */}
                <table className="w-full border-collapse">
                    <thead>
                        <tr>
                            <th className="border border-gray-400 px-4 py-2">Client Name</th>
                            <th className="border border-gray-400 px-4 py-2">Address</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.clients.map(client => (
                            <tr key={client.id}>
                                <td className="border border-gray-400 px-4 py-2">{client.Name}</td>
                                <td className="border border-gray-400 px-4 py-2">{client.emailId}</td>
                                <td className="border border-gray-400 px-4 py-2">
                                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2" onClick={() => this.editClient(client.id)}>Update</button>
                                    <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mr-2" onClick={() => this.deleteClient(client.id)}>Delete</button>
                                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" onClick={() => this.viewClient(client.id)}>View</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default ListClientComponent;
