import React from "react";
import { useState } from "react";

const employees = window.employees;
const clients = window.clients

    const DashboardAdmin = () => {
        const employees = [
            { id: 1, name: "John Doe" },
            { id: 2, name: "Jane Smith" },
            // Add more employees here
        ];

        const clients = [
            { id: 1, name: "Client A" },
            { id: 2, name: "Client B" },
            // Add more clients here
        ];

        const [selectedEmployee, setSelectedEmployee] = useState(null);

        const handleEmployeeClick = (employee) => {
            setSelectedEmployee(employee);
        };

        return (
            <>
                <h1 className="text-black text-2xl text-center font-bold mb-4">
                    Dashboard Admin
                </h1>

                <div className="flex justify-center">
                    <div className="grid grid-cols-2 gap-11 mt-11 border">
                        <div>
                            <h2 className="text-black text-3xl font-bold mb-4 border">
                                Liste des employés :
                            </h2>
                            <ul className="text-black text-xl">
                                {employees.map((employee) => (
                                    <li
                                        key={employee.id}
                                        onClick={() => handleEmployeeClick(employee)}
                                    >
                                        {employee.name}
                                    </li>
                                ))}
                            </ul>
                        </div>
                        <div>
                            <h2 className="text-black text-3xl mb-4 font-bold border">
                                Liste des clients :
                            </h2>
                            <ul className="text-black text-xl">
                                {clients.map((client) => (
                                    <li key={client.id}>{client.name}</li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>

                {selectedEmployee && (
                    <div>
                        <h2>Calendrier de {selectedEmployee.name}</h2>
                        {/* Add calendar component here */}
                    </div>
                )}
            </>
        );
    };

    <>

            <h1 className="text-black text-2xl text-center font-bold mb-4">Dashboard Admin</h1>

            <div className="flex justify-center">
                <div className="grid grid-cols-2 gap-11 mt-11 border">
                    <div>
                        <h2 className="text-black text-3xl font-bold mb-4 border">
                            Liste des employés :
                        </h2>
                        <ul className="text-black text-xl">
                            
                            {employees.map((employee) => (
                                <li key={employee.id}>{employee.name}</li>
                            ))}
                        </ul>
                    </div>
                    <div>
                        <h2 className="text-black text-3xl mb-4 font-bold border">Liste des clients :</h2>
                        <ul className="text-black text-xl">
                            {clients.map((client) => (
                                <li key={client.id}>{client.name}</li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
    </>

export default DashboardAdmin;
