import axios from 'axios';

const  EMPLOYEE_API_BASE_URL  =  "http://localhost:8080/api/v1/employees" ; 

class  EmployeeService  { 

    getEmployees ( ) { 
        return  axios . obtenir ( EMPLOYEE_API_BASE_URL ) ; 
    } 

    createEmployee ( employé ) { 
        return  axios . poste ( EMPLOYEE_API_BASE_URL ,  employé ) ; 
    } 

    getEmployeeById ( employeeId ) { 
        return  axios . get ( EMPLOYEE_API_BASE_URL  +  '/'  +  employeId ) ; 
    } 

    updateEmployee ( employé ,  employéId ) { 
        return  axios . put ( EMPLOYEE_API_BASE_URL  +  '/'  +  EmployeeId ,  employé ) ; 
    } 

    deleteEmployee ( EmployeeId ) { 
        return  axios . supprimer ( EMPLOYEE_API_BASE_URL  +  '/'  +  employeId ) ; 
    } 
} 

export default  new  EmployeeService ( );
