import React from "react";

const HR = () => {
    
    const employees = [
        { name: "John", location: "New York" },
        { name: "Jane", location: "London" },
        { name: "Mike", location: "Tokyo" },
        { name: "Emily", location: "Paris" },
        // Add more employees here
    ];
    const clients = [
        { name: "Apple", location: "California" },
        { name: "Microsoft", location: "Washington" },
        { name: "Google", location: "California" },
        { name: "Amazon", location: "Washington" },
        // Add more clients here
    ];

    const [selectedEmployee, setSelectedEmployee] = React.useState(null);

    const handleEmployeeClick = (employee) => {
        setSelectedEmployee(employee);
        // Open modal or perform any other action
    };

    return (
        <div style={{ display: "flex" }}>
            <div style={{ flex: 1 }}>
                <h1>Employees</h1>
                <ul className="space-y-2">
                    {employees.map((employee) => (
                        <li
                            key={employee.name}
                            className="cursor-pointer"
                            onClick={() => handleEmployeeClick(employee)}
                        >
                            {employee.name} - {employee.location}
                        </li>
                    ))}
                </ul>
            </div>
            <div style={{ flex: 1 }}>
                <h1>Clients</h1>
                <ul className="space-y-2">
                    {clients.map((client) => (
                        <li key={client.name}>{client.name} - {client.location}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default HR;
