import React from "react";

const HR = () => {
    const employees = [
        { name: "John", location: "Home" },
        { name: "Jane", location: "Client" },
        { name: "Mike", location: "Home" },
        { name: "Emily", location: "Office" },
        // Add more employees here
    ];
    const clients = [
        { name: "Apple", location: "Bruxelles" },
        { name: "Microsoft", location: "Washington" },
        { name: "Google", location: "California" },
        { name: "Amazon", location: "Washington" },
        // Add more clients here
    ];

    const [selectedEmployee, setSelectedEmployee] = React.useState(null);

    const handleEmployeeClick = (employee) => {
        setSelectedEmployee(employee);
    };

    return (
        <div className="grid grid-cols-2 gap-4 justify-center">
            <div>
                <h1>Employees</h1>
                <ul className="space-y-2">
                    {employees.map((employee) => (
                        <li
                            key={employee.name}
                            className="cursor-pointer"
                            onClick={() => handleEmployeeClick(employee)}
                        >
                            {employee.name} - {employee.location}
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                <h1>Clients</h1>
                <ul className="space-y-2">
                    {clients.map((client) => (
                        <li key={client.name}>
                            {client.name} - {client.location}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default HR;
